mapper.select:
  /* 2021.02.21 choi.hak.jun selectBoardList*/
  SELECT board_id, title, writer_id, writer_name, content, view_count, like_count, code, create_by, date_format(create_time, '%Y-%m-%d %H:%i') as create_time
    FROM board
   WHERE code = #{code} AND would_you_yn = 1
ORDER BY board_id desc;
